mapper.select:
select id, setmeal_id, dish_id, name, price, copies
from setmeal_dish where dish_id in

    #{id}

mapper.delete:
delete from setmeal_dish
where setmeal_id in 

    #{setMealId}

mapper.select:
select id, setmeal_id, dish_id, name, price, copies
from setmeal_dish
where setmeal_id = #{setMealId}
